import{_ as r,o as a,c as e,e as h}from"./app.91a036f7.js";var n="/blog/assets/1.4a1efd62.jpg",o="/blog/assets/2.3be0bdc5.jpg",t="/blog/assets/3.9438dbd2.jpg";const d={},s=h('<h1 id="\u5B8F\u89C2\u89C6\u89D2\u4E0B\u7684\u6D4F\u89C8\u5668" tabindex="-1"><a class="header-anchor" href="#\u5B8F\u89C2\u89C6\u89D2\u4E0B\u7684\u6D4F\u89C8\u5668" aria-hidden="true">#</a> \u5B8F\u89C2\u89C6\u89D2\u4E0B\u7684\u6D4F\u89C8\u5668</h1><h2 id="\u7EBF\u7A0B-vs-\u8FDB\u7A0B" tabindex="-1"><a class="header-anchor" href="#\u7EBF\u7A0B-vs-\u8FDB\u7A0B" aria-hidden="true">#</a> \u7EBF\u7A0B VS \u8FDB\u7A0B</h2><p>\u591A\u7EBF\u7A0B\u53EF\u4EE5\u5E76\u884C\u5904\u7406\u4EFB\u52A1\uFF0C\u4F46\u662F<strong>\u7EBF\u7A0B\u662F\u4E0D\u80FD\u5355\u72EC\u5B58\u5728\u7684\uFF0C\u5B83\u662F\u7531\u8FDB\u7A0B\u6765\u542F\u52A8\u548C\u7BA1\u7406\u7684\u3002</strong></p><p><strong>\u4E00\u4E2A\u8FDB\u7A0B\u5C31\u662F\u4E00\u4E2A\u7A0B\u5E8F\u7684\u8FD0\u884C\u5B9E\u4F8B</strong>\u3002\u8BE6\u7EC6\u89E3\u91CA\u5C31\u662F\uFF0C\u542F\u52A8\u4E00\u4E2A\u7A0B\u5E8F\u7684\u65F6\u5019\uFF0C\u64CD\u4F5C\u7CFB\u7EDF\u4F1A\u4E3A\u8BE5\u7A0B\u5E8F\u521B\u5EFA\u4E00\u5757\u5185\u5B58\uFF0C\u7528\u6765\u5B58\u653E\u4EE3\u7801\u3001\u8FD0\u884C\u4E2D\u7684\u6570\u636E\u548C\u4E00\u4E2A\u6267\u884C\u4EFB\u52A1\u7684\u4E3B\u7EBF\u7A0B\uFF0C\u6211\u4EEC\u628A\u8FD9\u6837\u7684\u4E00\u4E2A\u8FD0\u884C\u73AF\u5883\u53EB\u8FDB\u7A0B\u3002</p><p><strong>\u7EBF\u7A0B\u662F\u4F9D\u9644\u4E8E\u8FDB\u7A0B\u7684\uFF0C\u800C\u8FDB\u7A0B\u4E2D\u4F7F\u7528\u591A\u7EBF\u7A0B\u5E76\u884C\u5904\u7406\u80FD\u63D0\u5347\u8FD0\u7B97\u6548\u7387\u3002</strong></p><h3 id="\u8FDB\u7A0B\u548C\u7EBF\u7A0B\u4E4B\u95F4\u7684\u5173\u7CFB" tabindex="-1"><a class="header-anchor" href="#\u8FDB\u7A0B\u548C\u7EBF\u7A0B\u4E4B\u95F4\u7684\u5173\u7CFB" aria-hidden="true">#</a> \u8FDB\u7A0B\u548C\u7EBF\u7A0B\u4E4B\u95F4\u7684\u5173\u7CFB</h3><ul><li>\u8FDB\u7A0B\u4E2D\u7684\u4EFB\u610F\u4E00\u7EBF\u7A0B\u6267\u884C\u51FA\u9519\uFF0C\u90FD\u4F1A\u5BFC\u81F4\u6574\u4E2A\u8FDB\u7A0B\u7684\u5D29\u6E83\u3002</li><li>\u7EBF\u7A0B\u4E4B\u95F4<strong>\u5171\u4EAB\u8FDB\u7A0B\u4E2D\u7684\u6570\u636E</strong>\u3002</li><li>\u5F53\u4E00\u4E2A\u8FDB\u7A0B\u5173\u95ED\u4E4B\u540E\uFF0C\u64CD\u4F5C\u7CFB\u7EDF<strong>\u4F1A\u56DE\u6536\u8FDB\u7A0B\u6240\u5360\u7528\u7684\u5185\u5B58</strong>\u3002</li><li>\u8FDB\u7A0B\u4E4B\u95F4\u7684\u5185\u5BB9\u76F8\u4E92\u9694\u79BB\u3002</li></ul><h2 id="\u5355\u8FDB\u7A0B\u6D4F\u89C8\u5668\u65F6\u4EE3\u7684\u95EE\u9898" tabindex="-1"><a class="header-anchor" href="#\u5355\u8FDB\u7A0B\u6D4F\u89C8\u5668\u65F6\u4EE3\u7684\u95EE\u9898" aria-hidden="true">#</a> \u5355\u8FDB\u7A0B\u6D4F\u89C8\u5668\u65F6\u4EE3\u7684\u95EE\u9898</h2><h3 id="\u4E0D\u7A33\u5B9A" tabindex="-1"><a class="header-anchor" href="#\u4E0D\u7A33\u5B9A" aria-hidden="true">#</a> \u4E0D\u7A33\u5B9A</h3><ul><li>\u65E9\u671F\u6D4F\u89C8\u5668\u9700\u8981\u501F\u52A9\u4E8E\u63D2\u4EF6\u6765\u5B9E\u73B0\u8BF8\u5982 Web \u89C6\u9891\u3001Web \u6E38\u620F\u7B49\u5404\u79CD\u5F3A\u5927\u7684\u529F\u80FD\uFF0C\u4F46\u662F\u63D2\u4EF6\u662F\u6700\u5BB9\u6613\u51FA\u95EE\u9898\u7684\u6A21\u5757\uFF0C\u5E76\u4E14\u8FD8\u8FD0\u884C\u5728\u6D4F\u89C8\u5668\u8FDB\u7A0B\u4E4B\u4E2D\uFF0C\u6240\u4EE5<strong>\u4E00\u4E2A\u63D2\u4EF6\u7684\u610F\u5916\u5D29\u6E83\u4F1A\u5F15\u8D77\u6574\u4E2A\u6D4F\u89C8\u5668\u7684\u5D29\u6E83\u3002</strong></li><li>\u9664\u4E86\u63D2\u4EF6\u4E4B\u5916\uFF0C\u6E32\u67D3\u5F15\u64CE\u6A21\u5757\u4E5F\u662F\u4E0D\u7A33\u5B9A\u7684\uFF0C\u901A\u5E38\u4E00\u4E9B\u590D\u6742\u7684 JavaScript \u4EE3\u7801\u5C31\u6709\u53EF\u80FD\u5F15\u8D77\u6E32\u67D3\u5F15\u64CE\u6A21\u5757\u7684\u5D29\u6E83\u3002\u548C\u63D2\u4EF6\u4E00\u6837\uFF0C\u6E32\u67D3\u5F15\u64CE\u7684\u5D29\u6E83\u4E5F\u4F1A\u5BFC\u81F4\u6574\u4E2A\u6D4F\u89C8\u5668\u7684\u5D29\u6E83\u3002</li></ul><h3 id="\u4E0D\u6D41\u7545" tabindex="-1"><a class="header-anchor" href="#\u4E0D\u6D41\u7545" aria-hidden="true">#</a> \u4E0D\u6D41\u7545</h3><ul><li>\u5355\u4E2ATab\u9875\u7684\u6B7B\u5FAA\u73AF\u4F1A\u7275\u626F\u6574\u4E2A\u6D4F\u89C8\u5668\u7684\u5361\u987F\u3001\u5931\u53BB\u54CD\u5E94\u3002</li><li>\u590D\u6742\u9875\u9762\u7684\u6253\u5F00\u548C\u5173\u95ED\uFF0C<strong>\u4E0D\u80FD\u5B8C\u5168\u56DE\u6536\u5185\u5B58</strong>\uFF0C\u968F\u7740\u4F7F\u7528\u65F6\u95F4\u8D8A\u6765\u8D8A\u957F\uFF0C\u6D4F\u89C8\u5668\u4F1A\u53D8\u5F97\u8D8A\u6765\u8D8A\u6162\u3002</li></ul><h3 id="\u4E0D\u5B89\u5168" tabindex="-1"><a class="header-anchor" href="#\u4E0D\u5B89\u5168" aria-hidden="true">#</a> \u4E0D\u5B89\u5168</h3><ul><li>\u63D2\u4EF6\u53EF\u4EE5\u4F7F\u7528<code>C/C++</code>\u7B49\u4EE3\u7801\u7F16\u5199\uFF0C\u901A\u8FC7\u63D2\u4EF6\u53EF\u4EE5\u83B7\u53D6\u5230<strong>\u64CD\u4F5C\u7CFB\u7EDF\u7684\u4EFB\u610F\u8D44\u6E90</strong>\u3002</li><li>\u81F3\u4E8E\u9875\u9762\u811A\u672C\uFF0C\u5B83\u53EF\u4EE5\u901A\u8FC7\u6D4F\u89C8\u5668\u7684\u6F0F\u6D1E\u6765\u83B7\u53D6<strong>\u7CFB\u7EDF\u6743\u9650</strong>\u3002</li></ul><h2 id="\u591A\u8FDB\u7A0B\u6D4F\u89C8\u5668\u65F6\u4EE3" tabindex="-1"><a class="header-anchor" href="#\u591A\u8FDB\u7A0B\u6D4F\u89C8\u5668\u65F6\u4EE3" aria-hidden="true">#</a> \u591A\u8FDB\u7A0B\u6D4F\u89C8\u5668\u65F6\u4EE3</h2><h3 id="\u76EE\u524D\u591A\u8FDB\u7A0B\u67B6\u6784" tabindex="-1"><a class="header-anchor" href="#\u76EE\u524D\u591A\u8FDB\u7A0B\u67B6\u6784" aria-hidden="true">#</a> \u76EE\u524D\u591A\u8FDB\u7A0B\u67B6\u6784</h3><p><img src="'+n+'" alt="\u591A\u8FDB\u7A0B\u67B6\u6784"></p><h4 id="\u6D4F\u89C8\u5668\u4E3B\u8FDB\u7A0B" tabindex="-1"><a class="header-anchor" href="#\u6D4F\u89C8\u5668\u4E3B\u8FDB\u7A0B" aria-hidden="true">#</a> \u6D4F\u89C8\u5668\u4E3B\u8FDB\u7A0B</h4><p>\u4E3B\u8981\u8D1F\u8D23<strong>\u754C\u9762\u663E\u793A</strong>\u3001<strong>\u7528\u6237\u4EA4\u4E92</strong>\u3001<strong>\u5B50\u8FDB\u7A0B\u7BA1\u7406</strong>\uFF0C\u540C\u65F6\u63D0\u4F9B<strong>\u5B58\u50A8</strong>\u7B49\u529F\u80FD\u3002</p><h4 id="\u6E32\u67D3\u8FDB\u7A0B-n" tabindex="-1"><a class="header-anchor" href="#\u6E32\u67D3\u8FDB\u7A0B-n" aria-hidden="true">#</a> \u6E32\u67D3\u8FDB\u7A0B*n</h4><p>\u6838\u5FC3\u4EFB\u52A1\u662F<strong>\u5C06 HTML\u3001CSS \u548C JavaScript \u8F6C\u6362\u4E3A\u7528\u6237\u53EF\u4EE5\u4E0E\u4E4B\u4EA4\u4E92\u7684\u7F51\u9875</strong>\uFF0C<code>\u6392\u7248\u5F15\u64CE Blink</code>\u548C<code>JavaScript \u5F15\u64CE V8</code>\u90FD\u662F\u8FD0\u884C\u5728\u8BE5\u8FDB\u7A0B\u4E2D\uFF0C\u9ED8\u8BA4\u60C5\u51B5\u4E0B\uFF0CChrome \u4F1A<strong>\u4E3A\u6BCF\u4E2A Tab \u6807\u7B7E\u521B\u5EFA\u4E00\u4E2A\u6E32\u67D3\u8FDB\u7A0B</strong>\u3002\u51FA\u4E8E\u5B89\u5168\u8003\u8651\uFF0C\u6E32\u67D3\u8FDB\u7A0B\u90FD\u662F\u8FD0\u884C\u5728\u6C99\u7BB1\u6A21\u5F0F\u4E0B\u3002</p><h4 id="gpu\u8FDB\u7A0B" tabindex="-1"><a class="header-anchor" href="#gpu\u8FDB\u7A0B" aria-hidden="true">#</a> GPU\u8FDB\u7A0B</h4><p>\u5176\u5B9E\uFF0CChrome \u521A\u5F00\u59CB\u53D1\u5E03\u7684\u65F6\u5019\u662F\u6CA1\u6709<code>GPU</code>\u8FDB\u7A0B\u7684\u3002<br> \u800C GPU \u7684\u4F7F\u7528<strong>\u521D\u8877\u662F\u4E3A\u4E86\u5B9E\u73B0<code>3D CSS</code>\u7684\u6548\u679C</strong>\uFF0C\u53EA\u662F\u968F\u540E<strong>\u7F51\u9875\u3001Chrome \u7684 UI \u754C\u9762\u90FD\u9009\u62E9\u91C7\u7528<code>GPU</code>\u6765\u7ED8\u5236</strong>\uFF0C\u8FD9\u4F7F\u5F97<code>GPU</code>\u6210\u4E3A\u6D4F\u89C8\u5668\u666E\u904D\u7684\u9700\u6C42\u3002\u6700\u540E\uFF0CChrome \u5728\u5176\u591A\u8FDB\u7A0B\u67B6\u6784\u4E0A\u4E5F\u5F15\u5165\u4E86GPU \u8FDB\u7A0B\u3002</p><h4 id="\u7F51\u7EDC\u8FDB\u7A0B" tabindex="-1"><a class="header-anchor" href="#\u7F51\u7EDC\u8FDB\u7A0B" aria-hidden="true">#</a> \u7F51\u7EDC\u8FDB\u7A0B</h4><p>\u4E3B\u8981\u8D1F\u8D23\u9875\u9762\u7684<strong>\u7F51\u7EDC\u8D44\u6E90\u52A0\u8F7D</strong>\uFF0C\u4E4B\u524D\u662F\u4F5C\u4E3A\u4E00\u4E2A\u6A21\u5757\u8FD0\u884C\u5728\u6D4F\u89C8\u5668\u8FDB\u7A0B\u91CC\u9762\u7684\uFF0C\u76F4\u81F3\u6700\u8FD1\u624D\u72EC\u7ACB\u51FA\u6765\uFF0C\u6210\u4E3A\u4E00\u4E2A\u5355\u72EC\u7684\u8FDB\u7A0B\u3002</p><h4 id="\u63D2\u4EF6\u8FDB\u7A0B-n" tabindex="-1"><a class="header-anchor" href="#\u63D2\u4EF6\u8FDB\u7A0B-n" aria-hidden="true">#</a> \u63D2\u4EF6\u8FDB\u7A0B*n</h4><p>\u4E3B\u8981\u662F\u8D1F\u8D23\u63D2\u4EF6\u7684\u8FD0\u884C\uFF0C<strong>\u56E0\u63D2\u4EF6\u6613\u5D29\u6E83\uFF0C\u6240\u4EE5\u9700\u8981\u901A\u8FC7\u63D2\u4EF6\u8FDB\u7A0B\u6765\u9694\u79BB</strong>\uFF0C\u4EE5\u4FDD\u8BC1\u63D2\u4EF6\u8FDB\u7A0B\u5D29\u6E83\u4E0D\u4F1A\u5BF9\u6D4F\u89C8\u5668\u548C\u9875\u9762\u9020\u6210\u5F71\u54CD\u3002</p><h3 id="\u5982\u4F55\u89E3\u51B3\u5355\u8FDB\u7A0B\u67B6\u6784\u4E2D\u9047\u5230\u7684\u95EE\u9898" tabindex="-1"><a class="header-anchor" href="#\u5982\u4F55\u89E3\u51B3\u5355\u8FDB\u7A0B\u67B6\u6784\u4E2D\u9047\u5230\u7684\u95EE\u9898" aria-hidden="true">#</a> \u5982\u4F55\u89E3\u51B3\u5355\u8FDB\u7A0B\u67B6\u6784\u4E2D\u9047\u5230\u7684\u95EE\u9898</h3><h4 id="\u5982\u4F55\u89E3\u51B3\u4E0D\u7A33\u5B9A\u7684\u95EE\u9898" tabindex="-1"><a class="header-anchor" href="#\u5982\u4F55\u89E3\u51B3\u4E0D\u7A33\u5B9A\u7684\u95EE\u9898" aria-hidden="true">#</a> \u5982\u4F55\u89E3\u51B3\u4E0D\u7A33\u5B9A\u7684\u95EE\u9898\uFF1F</h4><p>\u7531\u4E8E\u8FDB\u7A0B\u662F\u76F8\u4E92\u9694\u79BB\u7684\uFF0C\u6240\u4EE5\u5F53\u4E00\u4E2A\u9875\u9762\u6216\u8005\u63D2\u4EF6\u5D29\u6E83 \u65F6\uFF0C\u5F71\u54CD\u5230\u7684\u4EC5\u4EC5\u662F\u5F53\u524D\u7684\u9875\u9762\u8FDB\u7A0B\u6216\u8005\u63D2\u4EF6\u8FDB\u7A0B\uFF0C\u5E76\u4E0D\u4F1A\u5F71\u54CD\u5230\u6D4F\u89C8\u5668\u548C\u5176\u4ED6\u9875\u9762\u3002</p><h4 id="\u5982\u4F55\u89E3\u51B3\u4E0D\u6D41\u7545\u7684\u95EE\u9898" tabindex="-1"><a class="header-anchor" href="#\u5982\u4F55\u89E3\u51B3\u4E0D\u6D41\u7545\u7684\u95EE\u9898" aria-hidden="true">#</a> \u5982\u4F55\u89E3\u51B3\u4E0D\u6D41\u7545\u7684\u95EE\u9898\uFF1F</h4><p>JavaScript \u4E5F\u662F\u8FD0\u884C\u5728\u6E32\u67D3\u8FDB\u7A0B\u4E2D\u7684\uFF0C\u6240\u4EE5\u5373\u4F7F JavaScript \u963B\u585E\u4E86\u6E32\u67D3\u8FDB\u7A0B\uFF0C\u5F71\u54CD\u5230\u7684\u4E5F\u53EA\u662F\u5F53\u524D\u7684\u6E32\u67D3\u9875\u9762\uFF0C\u800C\u5E76\u4E0D\u4F1A\u5F71\u54CD\u6D4F\u89C8\u5668\u548C\u5176\u4ED6\u9875\u9762\u3002</p><p>\u5BF9\u4E8E\u5185\u5B58\u6CC4\u6F0F\u7684\u89E3\u51B3\u65B9\u6CD5\u90A3\u5C31\u66F4\u7B80\u5355\u4E86\uFF0C\u56E0\u4E3A\u5F53\u5173\u95ED\u4E00\u4E2A\u9875\u9762\u65F6\uFF0C\u6574\u4E2A\u6E32\u67D3\u8FDB\u7A0B\u4E5F\u4F1A\u88AB\u5173\u95ED\uFF0C\u4E4B\u540E<strong>\u8BE5\u8FDB\u7A0B\u6240\u5360\u7528\u7684\u5185\u5B58\u90FD\u4F1A\u88AB\u7CFB\u7EDF\u56DE\u6536</strong>\u3002</p><h4 id="\u5982\u4F55\u89E3\u51B3\u4E0D\u5B89\u5168\u7684\u95EE\u9898" tabindex="-1"><a class="header-anchor" href="#\u5982\u4F55\u89E3\u51B3\u4E0D\u5B89\u5168\u7684\u95EE\u9898" aria-hidden="true">#</a> \u5982\u4F55\u89E3\u51B3\u4E0D\u5B89\u5168\u7684\u95EE\u9898\uFF1F</h4><p>\u91C7\u7528\u591A\u8FDB\u7A0B\u67B6\u6784\u7684\u989D\u5916\u597D\u5904\u662F\u53EF\u4EE5\u4F7F\u7528<strong>\u5B89\u5168\u6C99\u7BB1</strong>\uFF0C\u4F60\u53EF\u4EE5\u628A\u6C99\u7BB1\u770B\u6210\u662F\u64CD\u4F5C\u7CFB\u7EDF\u7ED9\u8FDB\u7A0B\u4E0A\u4E86\u4E00\u628A\u9501\uFF0C\u6C99\u7BB1\u91CC\u9762\u7684\u7A0B\u5E8F\u53EF\u4EE5\u8FD0\u884C\uFF0C\u4F46\u662F\u4E0D\u80FD\u5728\u4F60\u7684\u786C\u76D8\u4E0A\u5199\u5165\u4EFB\u4F55\u6570\u636E\uFF0C\u4E5F\u4E0D\u80FD\u5728\u654F\u611F\u4F4D\u7F6E\u8BFB\u53D6\u4EFB\u4F55\u6570\u636E\uFF0C\u4F8B\u5982\u4F60\u7684\u6587\u6863\u548C\u684C\u9762\u3002<br> Chrome \u628A\u63D2\u4EF6\u8FDB\u7A0B\u548C\u6E32\u67D3\u8FDB\u7A0B\u9501\u5728\u6C99\u7BB1\u91CC\u9762\uFF0C\u8FD9\u6837\u5373\u4F7F\u5728\u6E32\u67D3\u8FDB\u7A0B\u6216\u8005\u63D2\u4EF6\u8FDB\u7A0B\u91CC\u9762\u6267\u884C\u4E86\u6076\u610F\u7A0B\u5E8F\uFF0C\u6076\u610F\u7A0B\u5E8F\u4E5F\u65E0\u6CD5\u7A81\u7834\u6C99\u7BB1\u53BB\u83B7\u53D6\u7CFB\u7EDF\u6743\u9650\u3002</p><h3 id="\u591A\u8FDB\u7A0B\u5E26\u6765\u7684\u95EE\u9898" tabindex="-1"><a class="header-anchor" href="#\u591A\u8FDB\u7A0B\u5E26\u6765\u7684\u95EE\u9898" aria-hidden="true">#</a> \u591A\u8FDB\u7A0B\u5E26\u6765\u7684\u95EE\u9898</h3><h4 id="\u66F4\u9AD8\u7684\u8D44\u6E90\u5360\u7528" tabindex="-1"><a class="header-anchor" href="#\u66F4\u9AD8\u7684\u8D44\u6E90\u5360\u7528" aria-hidden="true">#</a> \u66F4\u9AD8\u7684\u8D44\u6E90\u5360\u7528</h4><p>\u56E0\u4E3A\u6BCF\u4E2A\u8FDB\u7A0B\u90FD\u4F1A\u5305\u542B\u516C\u5171\u57FA\u7840\u7ED3\u6784\u7684\u526F\u672C\uFF08\u5982 JavaScript \u8FD0\u884C\u73AF\u5883\uFF09\uFF0C\u8FD9\u5C31\u610F\u5473\u7740\u6D4F\u89C8\u5668\u4F1A\u6D88\u8017\u66F4\u591A\u7684\u5185\u5B58\u8D44\u6E90\u3002</p><h4 id="\u66F4\u590D\u6742\u7684\u4F53\u7CFB\u67B6\u6784" tabindex="-1"><a class="header-anchor" href="#\u66F4\u590D\u6742\u7684\u4F53\u7CFB\u67B6\u6784" aria-hidden="true">#</a> \u66F4\u590D\u6742\u7684\u4F53\u7CFB\u67B6\u6784</h4><p>\u6D4F\u89C8\u5668\u5404\u6A21\u5757\u4E4B\u95F4\u8026\u5408\u6027\u9AD8\u3001\u6269\u5C55\u6027\u5DEE\u7B49\u95EE\u9898\uFF0C\u4F1A\u5BFC\u81F4\u73B0\u5728\u7684\u67B6\u6784\u5DF2\u7ECF\u5F88\u96BE\u9002\u5E94\u65B0\u7684\u9700\u6C42\u4E86\u3002</p><h2 id="\u672A\u6765\u9762\u5411\u670D\u52A1\u7684\u67B6\u6784" tabindex="-1"><a class="header-anchor" href="#\u672A\u6765\u9762\u5411\u670D\u52A1\u7684\u67B6\u6784" aria-hidden="true">#</a> \u672A\u6765\u9762\u5411\u670D\u52A1\u7684\u67B6\u6784</h2><p>\u6700\u521D\u7684\u6D4F\u89C8\u5668\u90FD\u662F\u5355\u8FDB\u7A0B\u7684\uFF0C\u5B83\u4EEC<strong>\u4E0D\u7A33\u5B9A\u3001\u4E0D\u6D41\u7545\u4E14\u4E0D\u5B89\u5168</strong>\uFF0C\u4E4B\u540E\u51FA\u73B0\u4E86 Chrome\uFF0C\u521B\u9020\u6027\u5730\u5F15\u5165\u4E86<strong>\u591A\u8FDB\u7A0B\u67B6\u6784</strong>\uFF0C\u5E76\u89E3\u51B3\u4E86\u8FD9\u4E9B\u9057\u7559\u95EE\u9898\u3002</p><p>\u968F\u540E Chrome \u8BD5\u56FE\u5E94\u7528\u5230\u66F4\u591A\u4E1A\u52A1\u573A\u666F\uFF0C\u5982<strong>\u79FB\u52A8\u8BBE\u5907\u3001VR\u3001\u89C6\u9891</strong>\u7B49\uFF0C\u4E3A\u4E86\u652F\u6301\u8FD9\u4E9B\u573A\u666F\uFF0CChrome \u7684\u67B6\u6784\u4F53\u7CFB\u53D8\u5F97\u8D8A\u6765\u8D8A\u590D\u6742\uFF0C\u8FD9\u79CD\u67B6\u6784\u7684\u590D\u6742\u6027\u5012\u903C Chrome \u5F00\u53D1\u56E2\u961F\u5FC5\u987B\u8FDB\u884C\u67B6\u6784\u7684\u91CD\u6784\uFF0C\u6700\u7EC8 Chrome \u56E2\u961F\u9009\u62E9\u4E86<strong>\u9762\u5411\u670D\u52A1\u67B6\u6784</strong>\uFF08SOA\uFF09\u5F62\u5F0F\uFF0C\u8FD9\u4E5F\u662F Chrome \u56E2\u961F\u73B0\u9636\u6BB5\u7684\u4E00\u4E2A\u4E3B\u8981\u4EFB\u52A1\u3002</p><p>\u4E3A\u4E86\u89E3\u51B3\u8FD9\u4E9B\u95EE\u9898\uFF0C\u5728 2016 \u5E74\uFF0CChrome \u5B98\u65B9\u56E2\u961F\u4F7F\u7528<strong>\u9762\u5411\u670D\u52A1\u7684\u67B6\u6784</strong>\uFF08Services Oriented Architecture\uFF0C\u7B80\u79F0SOA\uFF09\u7684\u601D\u60F3\u8BBE\u8BA1\u4E86\u65B0\u7684 Chrome \u67B6\u6784\u3002</p><p>\u4E5F\u5C31\u662F\u8BF4Chrome \u6574\u4F53\u67B6\u6784\u4F1A\u671D\u5411\u73B0\u4EE3\u64CD\u4F5C\u7CFB\u7EDF\u6240\u91C7\u7528\u7684\u9762\u5411\u670D\u52A1\u7684\u67B6\u6784\u65B9\u5411\u53D1\u5C55\uFF0C\u539F\u6765\u7684\u5404\u79CD\u6A21\u5757\u4F1A\u88AB\u91CD\u6784\u6210<strong>\u72EC\u7ACB\u7684\u670D\u52A1</strong>\uFF08Service\uFF09\uFF0C\u6BCF\u4E2A\u670D\u52A1\u90FD\u53EF\u4EE5\u5728\u72EC\u7ACB\u7684\u8FDB\u7A0B\u4E2D\u8FD0\u884C\uFF0C\u8BBF\u95EE\u670D\u52A1\u5FC5\u987B\u4F7F\u7528\u5B9A\u4E49\u597D\u7684\u63A5\u53E3\uFF0C\u901A\u8FC7<code>IPC</code>\u6765\u901A\u4FE1\uFF0C\u4ECE\u800C\u6784\u5EFA\u4E00\u4E2A<strong>\u66F4\u5185\u805A\u3001\u677E\u8026\u5408\u3001\u6613\u4E8E\u7EF4\u62A4\u548C\u6269\u5C55</strong>\u7684\u7CFB\u7EDF\uFF0C\u66F4\u597D\u5B9E\u73B0Chrome<strong>\u7B80\u5355\u3001\u7A33\u5B9A\u3001\u9AD8\u901F\u3001\u5B89\u5168</strong>\u7684\u76EE\u6807\u3002</p><p>Chrome \u6700\u7EC8\u8981\u628A <strong>UI\u3001\u6570\u636E\u5E93\u3001\u6587\u4EF6\u3001\u8BBE\u5907\u3001\u7F51\u7EDC</strong>\u7B49\u6A21\u5757\u91CD\u6784\u4E3A\u57FA\u7840\u670D\u52A1\u3002 <img src="'+o+'" alt="SOA\u8FDB\u7A0B\u6A21\u578B\u56FE"></p><p>\u76EE\u524D Chrome \u6B63\u5904\u5728\u8001\u7684\u67B6\u6784\u5411\u670D\u52A1\u5316\u67B6\u6784\u8FC7\u6E21\u9636\u6BB5\uFF0C\u8FD9\u5C06\u662F\u4E00\u4E2A\u6F2B\u957F\u7684\u8FED\u4EE3\u8FC7\u7A0B\u3002</p><p>Chrome \u6B63\u5728\u9010\u6B65\u6784\u5EFA Chrome <strong>\u57FA\u7840\u670D\u52A1</strong>\uFF08Chrome Foundation Service\uFF09\uFF0C\u5982\u679C\u8BA4\u4E3AChrome \u662F\u201C\u4FBF\u643A\u5F0F\u64CD\u4F5C\u7CFB\u7EDF\u201D\uFF0C\u90A3\u4E48 Chrome \u57FA\u7840\u670D\u52A1\u4FBF\u53EF\u4EE5\u88AB\u89C6\u4E3A\u8BE5\u64CD\u4F5C\u7CFB\u7EDF\u7684\u201C\u57FA\u7840\u201D\u7CFB\u7EDF\u670D\u52A1\u5C42\u3002</p><p>\u540C\u65F6 Chrome \u8FD8\u63D0\u4F9B\u7075\u6D3B\u7684\u5F39\u6027\u67B6\u6784\uFF0C\u5728\u5F3A\u5927\u6027\u80FD\u8BBE\u5907\u4E0A\u4F1A\u4EE5\u591A\u8FDB\u7A0B\u7684\u65B9\u5F0F\u8FD0\u884C\u57FA\u7840\u670D\u52A1\uFF0C\u4F46\u662F\u5982\u679C\u5728\u8D44\u6E90\u53D7\u9650\u7684\u8BBE\u5907\u4E0A\uFF0CChrome \u4F1A\u5C06\u5F88\u591A\u670D\u52A1\u6574\u5408\u5230\u4E00\u4E2A\u8FDB\u7A0B\u4E2D\uFF0C\u4ECE\u800C\u8282\u7701\u5185\u5B58\u5360\u7528\u3002 <img src="'+t+'" alt="\u8D44\u6E90\u4E0D\u8DB3\u7684\u8BBE\u5907\u4E0A\uFF0C\u5C06\u670D\u52A1\u5408\u5E76\u5230\u6D4F\u89C8\u5668\u8FDB\u7A0B\u4E2D"></p>',49),i=[s];function c(g,l){return a(),e("div",null,i)}var u=r(d,[["render",c],["__file","1.html.vue"]]);export{u as default};
