import{_ as e,o as l,c as i,e as a}from"./app.91a036f7.js";const h={},r=a('<h1 id="https" tabindex="-1"><a class="header-anchor" href="#https" aria-hidden="true">#</a> HTTPS</h1><blockquote><p>\u5176\u5B9E\u5B83\u5E76\u4E0D\u662F\u4E00\u4E2A\u65B0\u7684\u534F\u8BAE\uFF0C\u800C\u662F\u5728 HTTP \u4E0B\u9762\u589E\u52A0\u4E86\u4E00\u5C42 SSL/TLS \u534F\u8BAE\uFF0C\u7B80\u5355\u7684\u8BB2\uFF0CHTTPS = HTTP + SSL/TLS\uFF0C\u5373HTTP\u4E0B\u52A0\u5165SSL\u5C42\u3002</p></blockquote><h2 id="\u4EC0\u4E48\u662F-ssl-tls" tabindex="-1"><a class="header-anchor" href="#\u4EC0\u4E48\u662F-ssl-tls" aria-hidden="true">#</a> \u4EC0\u4E48\u662F SSL/TLS</h2><p>SSL \u5373\u5B89\u5168\u5957\u63A5\u5C42\uFF08Secure Sockets Layer\uFF09\uFF0C\u5728 OSI \u4E03\u5C42\u6A21\u578B\u4E2D\u5904\u4E8E\u4F1A\u8BDD\u5C42(\u7B2C 5 \u5C42)\u3002\u4E4B\u524D SSL \u51FA\u8FC7\u4E09\u4E2A\u5927\u7248\u672C\uFF0C\u5F53\u5B83\u53D1\u5C55\u5230\u7B2C\u4E09\u4E2A\u5927\u7248\u672C\u7684\u65F6\u5019\u624D\u88AB\u6807\u51C6\u5316\uFF0C\u6210\u4E3A TLS\uFF08\u4F20\u8F93\u5C42\u5B89\u5168\uFF0CTransport Layer Security\uFF09\uFF0C\u5E76\u88AB\u5F53\u505A TLS1.0 \u7684\u7248\u672C\uFF0C\u51C6\u786E\u5730\u8BF4\uFF0CTLS1.0 = SSL3.1\u3002<br> \u73B0\u5728\u4E3B\u6D41\u7684\u7248\u672C\u662F TLS/1.2, \u4E4B\u524D\u7684 TLS1.0\u3001TLS1.1 \u90FD\u88AB\u8BA4\u4E3A\u662F\u4E0D\u5B89\u5168\u7684\uFF0C\u5728\u4E0D\u4E45\u7684\u5C06\u6765\u4F1A\u88AB\u5B8C\u5168\u6DD8\u6C70\u3002</p><h2 id="\u5DE5\u4F5C\u539F\u7406" tabindex="-1"><a class="header-anchor" href="#\u5DE5\u4F5C\u539F\u7406" aria-hidden="true">#</a> \u5DE5\u4F5C\u539F\u7406</h2><ul><li>\u5BA2\u6237\u7AEF\u53D1\u9001\u8BF7\u6C42\u5230\u670D\u52A1\u7AEF</li><li>\u670D\u52A1\u7AEF\u5C06\u81EA\u8EAB\u7684\u76F8\u5173\u4FE1\u606F\u548C\u516C\u94A5\u7ED9\u5230CA</li><li>\u901A\u8FC7hash\u7B97\u6CD5\u5904\u7406\u8FD9\u4E2A\u76F8\u5173\u4FE1\u606F\u548C\u516C\u94A5\u5F97\u5230\u4E00\u4EFD\u6D88\u606F\u6458\u8981</li><li>CA\u7528\u81EA\u5DF1\u7684\u79C1\u94A5\u5C06\u5B83\u52A0\u5BC6\u6210\u6570\u5B57\u7B7E\u540D\uFF0C\u548C\u670D\u52A1\u7AEF\u63D0\u4F9B\u7684\u539F\u59CB\u7684\u516C\u94A5\u4EE5\u53CA\u81EA\u8EAB\u76F8\u5173\u4FE1\u606F\u4E00\u8D77\u4F5C\u4E3A\u6570\u5B57\u8BC1\u4E66\u7ED9\u5230\u670D\u52A1\u7AEF</li><li>\u670D\u52A1\u7AEF\u5C06\u8FD9\u4E2A\u6570\u5B57\u8BC1\u4E66\u7ED9\u5230\u5BA2\u6237\u7AEF</li><li>\u5BA2\u6237\u7AEF\u901A\u8FC7CA\u516C\u94A5\u5C06\u6570\u5B57\u7B7E\u8BC1\u89E3\u5BC6\u5F97\u5230\u516C\u94A5\u548C\u670D\u52A1\u7AEF\u7684\u76F8\u5173\u4FE1\u606F\uFF0C\u5E76\u548C\u6570\u5B57\u8BC1\u4E66\u4E2D\u643A\u5E26\u7684\u539F\u59CB\u4FE1\u606F\u8FDB\u884C\u6BD4\u5BF9</li><li>\u6BD4\u5BF9\u65E0\u8BEF\u540E\u5BA2\u6237\u7AEF\u5C06\u7ED9\u6570\u636E\u5BF9\u79F0\u52A0\u5BC6\u7684\u5BC6\u94A5\u901A\u8FC7\u521A\u5F97\u5230\u7684\u670D\u52A1\u7AEF\u516C\u94A5\u8FDB\u884C\u975E\u5BF9\u79F0\u52A0\u5BC6\uFF0C\u5E76\u7ED9\u5230\u670D\u52A1\u7AEF</li><li>\u670D\u52A1\u7AEF\u6536\u5230\u5E76\u7528\u81EA\u5DF1\u7684\u79C1\u94A5\u89E3\u5BC6\uFF0C\u81F3\u6B64\uFF0C\u53CC\u65B9\u90FD\u6709\u4E86\u6570\u636E\u5BF9\u79F0\u52A0\u5BC6\u7684\u5BC6\u94A5\uFF0C\u5E76\u53EF\u4EE5\u4EE5\u6B64\u4F20\u8F93\u6570\u636E</li></ul><h2 id="\u4E0Ehttp\u4E0D\u540C\u4E4B\u5904" tabindex="-1"><a class="header-anchor" href="#\u4E0Ehttp\u4E0D\u540C\u4E4B\u5904" aria-hidden="true">#</a> \u4E0EHTTP\u4E0D\u540C\u4E4B\u5904</h2><ul><li>\u7AEF\u53E3: 443</li><li>\u9700\u8981\u7533\u8BF7\u8BC1\u4E66\uFF0C\u8BC1\u4E66\u5927\u591A\u6536\u8D39</li><li>\u975E\u5BF9\u79F0\u52A0\u5BC6\u66F4\u6162\uFF0C\u6545\u800C\u8FD8\u662F\u5BF9\u6570\u636E\u8FDB\u884C\u5BF9\u79F0\u52A0\u5BC6\uFF0C\u53EA\u5BF9\u5BF9\u79F0\u52A0\u5BC6\u7684\u5BC6\u94A5\u8FDB\u884C\u975E\u5BF9\u79F0\u52A0\u5BC6</li></ul><h2 id="ssl-tls\u534F\u8BAE\u8FD0\u884C\u673A\u5236" tabindex="-1"><a class="header-anchor" href="#ssl-tls\u534F\u8BAE\u8FD0\u884C\u673A\u5236" aria-hidden="true">#</a> SSL/TLS\u534F\u8BAE\u8FD0\u884C\u673A\u5236</h2><h3 id="\u5982\u4F55\u4FDD\u8BC1\u516C\u94A5\u4E0D\u88AB\u7BE1\u6539" tabindex="-1"><a class="header-anchor" href="#\u5982\u4F55\u4FDD\u8BC1\u516C\u94A5\u4E0D\u88AB\u7BE1\u6539" aria-hidden="true">#</a> \u5982\u4F55\u4FDD\u8BC1\u516C\u94A5\u4E0D\u88AB\u7BE1\u6539</h3><p>\u89E3\u51B3\u65B9\u6CD5\uFF1A\u5C06\u516C\u94A5\u653E\u5728\u6570\u5B57\u8BC1\u4E66\u4E2D\u3002\u53EA\u8981\u8BC1\u4E66\u662F\u53EF\u4FE1\u7684\uFF0C\u516C\u94A5\u5C31\u662F\u53EF\u4FE1\u7684\u3002</p><h3 id="\u516C\u94A5\u52A0\u5BC6\u8BA1\u7B97\u91CF\u592A\u5927-\u5982\u4F55\u51CF\u5C11\u8017\u7528\u65F6\u95F4" tabindex="-1"><a class="header-anchor" href="#\u516C\u94A5\u52A0\u5BC6\u8BA1\u7B97\u91CF\u592A\u5927-\u5982\u4F55\u51CF\u5C11\u8017\u7528\u65F6\u95F4" aria-hidden="true">#</a> \u516C\u94A5\u52A0\u5BC6\u8BA1\u7B97\u91CF\u592A\u5927\uFF0C\u5982\u4F55\u51CF\u5C11\u8017\u7528\u65F6\u95F4</h3><p>\u89E3\u51B3\u65B9\u6CD5\uFF1A\u4F7F\u7528\u5BF9\u79F0\u79D8\u94A5\u52A0\u5BC6\u4FE1\u606F\uFF0C\u4F7F\u7528\u975E\u5BF9\u79F0\u79D8\u94A5\u52A0\u5BC6\u5BF9\u79F0\u79D8\u94A5\u672C\u8EAB</p><h3 id="\u57FA\u672C\u8FC7\u7A0B" tabindex="-1"><a class="header-anchor" href="#\u57FA\u672C\u8FC7\u7A0B" aria-hidden="true">#</a> \u57FA\u672C\u8FC7\u7A0B</h3><ol><li>\u5BA2\u6237\u7AEF\u5411\u670D\u52A1\u5668\u7AEF\u7D22\u8981\u5E76\u9A8C\u8BC1\u516C\u94A5\u3002</li><li>\u53CC\u65B9\u534F\u5546\u751F\u6210<code>\u5BF9\u8BDD\u5BC6\u94A5</code>\u3002</li><li>\u53CC\u65B9\u91C7\u7528<code>\u5BF9\u8BDD\u5BC6\u94A5</code>\u8FDB\u884C\u52A0\u5BC6\u901A\u4FE1\u3002</li></ol><p>\u4E0A\u65B9\u524D2\u6B65\u4E5F\u79F0\u4E3A<strong>\u63E1\u624B\u9636\u6BB5</strong>\uFF0C\u63E1\u624B\u9636\u6BB5\u8BE6\u7EC6\u8FC7\u7A0B\u5982\u4E0B</p><h4 id="_1-\u5BA2\u6237\u7AEF\u53D1\u51FA\u8BF7\u6C42-clienthello" tabindex="-1"><a class="header-anchor" href="#_1-\u5BA2\u6237\u7AEF\u53D1\u51FA\u8BF7\u6C42-clienthello" aria-hidden="true">#</a> 1.\u5BA2\u6237\u7AEF\u53D1\u51FA\u8BF7\u6C42\uFF08ClientHello\uFF09</h4><p>\u9996\u5148\uFF0C\u5BA2\u6237\u7AEF\uFF08\u901A\u5E38\u662F\u6D4F\u89C8\u5668\uFF09\u5148\u5411\u670D\u52A1\u5668\u53D1\u51FA\u52A0\u5BC6\u901A\u4FE1\u7684\u8BF7\u6C42\uFF0C\u8FD9\u88AB\u53EB\u505AClientHello\u8BF7\u6C42\u3002<br> \u5728\u8FD9\u4E00\u6B65\uFF0C\u5BA2\u6237\u7AEF\u4E3B\u8981\u5411\u670D\u52A1\u5668\u63D0\u4F9B\u4EE5\u4E0B\u4FE1\u606F\u3002</p><ul><li>\u652F\u6301\u7684\u534F\u8BAE\u7248\u672C\uFF0C\u6BD4\u5982TLS 1.0\u7248\u3002</li><li>\u4E00\u4E2A\u5BA2\u6237\u7AEF\u751F\u6210\u7684\u968F\u673A\u6570\uFF0C\u7A0D\u540E\u7528\u4E8E\u751F\u6210&quot;\u5BF9\u8BDD\u5BC6\u94A5&quot;\u3002</li><li>\u652F\u6301\u7684\u52A0\u5BC6\u65B9\u6CD5\uFF0C\u6BD4\u5982RSA\u516C\u94A5\u52A0\u5BC6\u3002</li><li>\u652F\u6301\u7684\u538B\u7F29\u65B9\u6CD5\u3002</li><li>\u670D\u52A1\u5668\u7684\u57DF\u540D\uFF0806\u5E74\u52A0\u7684\u6269\u5C55\uFF0C\u5426\u5219\u4E00\u4E2A\u670D\u52A1\u5668\u53EA\u80FD\u6709\u4E00\u4E2A\u6570\u5B57\u8BC1\u4E66\uFF0C\u5BF9\u4E8E\u865A\u62DF\u673A\u6765\u8BF4\uFF0C\u5F88\u4E0D\u65B9\u4FBF\uFF09</li></ul><h4 id="_2-\u670D\u52A1\u5668\u56DE\u5E94-severhello" tabindex="-1"><a class="header-anchor" href="#_2-\u670D\u52A1\u5668\u56DE\u5E94-severhello" aria-hidden="true">#</a> 2.\u670D\u52A1\u5668\u56DE\u5E94\uFF08SeverHello\uFF09</h4><p>\u670D\u52A1\u5668\u6536\u5230\u5BA2\u6237\u7AEF\u8BF7\u6C42\u540E\uFF0C\u5411\u5BA2\u6237\u7AEF\u53D1\u51FA\u56DE\u5E94\uFF0C\u8FD9\u53EB\u505ASeverHello\u3002\u670D\u52A1\u5668\u7684\u56DE\u5E94\u5305\u542B\u4EE5\u4E0B\u5185\u5BB9\u3002</p><ul><li>\u786E\u8BA4\u4F7F\u7528\u7684\u52A0\u5BC6\u901A\u4FE1\u534F\u8BAE\u7248\u672C\uFF0C\u6BD4\u5982TLS 1.0\u7248\u672C\u3002\u5982\u679C\u6D4F\u89C8\u5668\u4E0E\u670D\u52A1\u5668\u652F\u6301\u7684\u7248\u672C\u4E0D\u4E00\u81F4\uFF0C\u670D\u52A1\u5668\u5173\u95ED\u52A0\u5BC6\u901A\u4FE1\u3002</li><li>\u4E00\u4E2A\u670D\u52A1\u5668\u751F\u6210\u7684\u968F\u673A\u6570\uFF0C\u7A0D\u540E\u7528\u4E8E\u751F\u6210&quot;\u5BF9\u8BDD\u5BC6\u94A5&quot;\u3002</li><li>\u786E\u8BA4\u4F7F\u7528\u7684\u52A0\u5BC6\u65B9\u6CD5\uFF0C\u6BD4\u5982RSA\u516C\u94A5\u52A0\u5BC6\u3002</li><li>\u670D\u52A1\u5668\u8BC1\u4E66\u3002</li></ul><p>\u9664\u4E86\u4E0A\u9762\u8FD9\u4E9B\u4FE1\u606F\uFF0C\u5982\u679C\u670D\u52A1\u5668\u9700\u8981\u786E\u8BA4\u5BA2\u6237\u7AEF\u7684\u8EAB\u4EFD\uFF0C\u5C31\u4F1A\u518D\u5305\u542B\u4E00\u9879\u8BF7\u6C42\uFF0C\u8981\u6C42\u5BA2\u6237\u7AEF\u63D0\u4F9B&quot;\u5BA2\u6237\u7AEF\u8BC1\u4E66&quot;\u3002\u6BD4\u5982\uFF0C\u91D1\u878D\u673A\u6784\u5F80\u5F80\u53EA\u5141\u8BB8\u8BA4\u8BC1\u5BA2\u6237\u8FDE\u5165\u81EA\u5DF1\u7684\u7F51\u7EDC\uFF0C\u5C31\u4F1A\u5411\u6B63\u5F0F\u5BA2\u6237\u63D0\u4F9BUSB\u5BC6\u94A5\uFF0C\u91CC\u9762\u5C31\u5305\u542B\u4E86\u4E00\u5F20\u5BA2\u6237\u7AEF\u8BC1\u4E66\u3002</p><h4 id="_3-\u5BA2\u6237\u7AEF\u56DE\u5E94" tabindex="-1"><a class="header-anchor" href="#_3-\u5BA2\u6237\u7AEF\u56DE\u5E94" aria-hidden="true">#</a> 3.\u5BA2\u6237\u7AEF\u56DE\u5E94</h4><p>\u5BA2\u6237\u7AEF\u6536\u5230\u670D\u52A1\u5668\u56DE\u5E94\u4EE5\u540E\uFF0C\u9996\u5148\u9A8C\u8BC1\u670D\u52A1\u5668\u8BC1\u4E66\u3002\u5982\u679C\u8BC1\u4E66\u4E0D\u662F\u53EF\u4FE1\u673A\u6784\u9881\u5E03\u3001\u6216\u8005\u8BC1\u4E66\u4E2D\u7684\u57DF\u540D\u4E0E\u5B9E\u9645\u57DF\u540D\u4E0D\u4E00\u81F4\u3001\u6216\u8005\u8BC1\u4E66\u5DF2\u7ECF\u8FC7\u671F\uFF0C\u5C31\u4F1A\u5411\u8BBF\u95EE\u8005\u663E\u793A\u4E00\u4E2A\u8B66\u544A\uFF0C\u7531\u5176\u9009\u62E9\u662F\u5426\u8FD8\u8981\u7EE7\u7EED\u901A\u4FE1\u3002<br> \u5982\u679C\u8BC1\u4E66\u6CA1\u6709\u95EE\u9898\uFF0C\u5BA2\u6237\u7AEF\u5C31\u4F1A\u4ECE\u8BC1\u4E66\u4E2D\u53D6\u51FA\u670D\u52A1\u5668\u7684\u516C\u94A5\u3002\u7136\u540E\uFF0C\u5411\u670D\u52A1\u5668\u53D1\u9001\u4E0B\u9762\u4E09\u9879\u4FE1\u606F\u3002</p><ul><li>\u4E00\u4E2A\u968F\u673A\u6570\u3002\u8BE5\u968F\u673A\u6570\u7528\u670D\u52A1\u5668\u516C\u94A5\u52A0\u5BC6\uFF0C\u9632\u6B62\u88AB\u7A83\u542C\u3002</li><li>\u7F16\u7801\u6539\u53D8\u901A\u77E5\uFF0C\u8868\u793A\u968F\u540E\u7684\u4FE1\u606F\u90FD\u5C06\u7528\u53CC\u65B9\u5546\u5B9A\u7684\u52A0\u5BC6\u65B9\u6CD5\u548C\u5BC6\u94A5\u53D1\u9001\u3002</li><li>\u5BA2\u6237\u7AEF\u63E1\u624B\u7ED3\u675F\u901A\u77E5\uFF0C\u8868\u793A\u5BA2\u6237\u7AEF\u7684\u63E1\u624B\u9636\u6BB5\u5DF2\u7ECF\u7ED3\u675F\u3002\u8FD9\u4E00\u9879\u540C\u65F6\u4E5F\u662F\u524D\u9762\u53D1\u9001\u7684\u6240\u6709\u5185\u5BB9\u7684hash\u503C\uFF0C\u7528\u6765\u4F9B\u670D\u52A1\u5668\u6821\u9A8C\u3002</li></ul><p>\u4E0A\u9762\u7B2C\u4E00\u9879\u7684\u968F\u673A\u6570\uFF0C\u662F\u6574\u4E2A\u63E1\u624B\u9636\u6BB5\u51FA\u73B0\u7684\u7B2C\u4E09\u4E2A\u968F\u673A\u6570\uFF0C\u53C8\u79F0&quot;pre-master key&quot;\u3002\u6709\u4E86\u5B83\u4EE5\u540E\uFF0C\u5BA2\u6237\u7AEF\u548C\u670D\u52A1\u5668\u5C31\u540C\u65F6\u6709\u4E86\u4E09\u4E2A\u968F\u673A\u6570\uFF0C\u63A5\u7740\u53CC\u65B9\u5C31\u7528\u4E8B\u5148\u5546\u5B9A\u7684\u52A0\u5BC6\u65B9\u6CD5\uFF0C\u5404\u81EA\u751F\u6210\u672C\u6B21\u4F1A\u8BDD\u6240\u7528\u7684\u540C\u4E00\u628A&quot;\u4F1A\u8BDD\u5BC6\u94A5&quot;\u3002</p><p>\u6B64\u5916\uFF0C\u5982\u679C\u524D\u4E00\u6B65\uFF0C\u670D\u52A1\u5668\u8981\u6C42\u5BA2\u6237\u7AEF\u8BC1\u4E66\uFF0C\u5BA2\u6237\u7AEF\u4F1A\u5728\u8FD9\u4E00\u6B65\u53D1\u9001\u8BC1\u4E66\u53CA\u76F8\u5173\u4FE1\u606F\u3002</p><h4 id="_4-\u670D\u52A1\u5668\u7684\u6700\u540E\u56DE\u5E94" tabindex="-1"><a class="header-anchor" href="#_4-\u670D\u52A1\u5668\u7684\u6700\u540E\u56DE\u5E94" aria-hidden="true">#</a> 4.\u670D\u52A1\u5668\u7684\u6700\u540E\u56DE\u5E94</h4><p>\u670D\u52A1\u5668\u6536\u5230\u5BA2\u6237\u7AEF\u7684\u7B2C\u4E09\u4E2A\u968F\u673A\u6570pre-master key\u4E4B\u540E\uFF0C\u8BA1\u7B97\u751F\u6210\u672C\u6B21\u4F1A\u8BDD\u6240\u7528\u7684&quot;\u4F1A\u8BDD\u5BC6\u94A5&quot;\u3002\u7136\u540E\uFF0C\u5411\u5BA2\u6237\u7AEF\u6700\u540E\u53D1\u9001\u4E0B\u9762\u4FE1\u606F\u3002</p><ul><li>\u7F16\u7801\u6539\u53D8\u901A\u77E5\uFF0C\u8868\u793A\u968F\u540E\u7684\u4FE1\u606F\u90FD\u5C06\u7528\u53CC\u65B9\u5546\u5B9A\u7684\u52A0\u5BC6\u65B9\u6CD5\u548C\u5BC6\u94A5\u53D1\u9001\u3002</li><li>\u670D\u52A1\u5668\u63E1\u624B\u7ED3\u675F\u901A\u77E5\uFF0C\u8868\u793A\u670D\u52A1\u5668\u7684\u63E1\u624B\u9636\u6BB5\u5DF2\u7ECF\u7ED3\u675F\u3002\u8FD9\u4E00\u9879\u540C\u65F6\u4E5F\u662F\u524D\u9762\u53D1\u9001\u7684\u6240\u6709\u5185\u5BB9\u7684hash\u503C\uFF0C\u7528\u6765\u4F9B\u5BA2\u6237\u7AEF\u6821\u9A8C\u3002</li></ul><p>\u81F3\u6B64\uFF0C\u6574\u4E2A\u63E1\u624B\u9636\u6BB5\u5168\u90E8\u7ED3\u675F\u3002\u63A5\u4E0B\u6765\uFF0C\u5BA2\u6237\u7AEF\u4E0E\u670D\u52A1\u5668\u8FDB\u5165\u52A0\u5BC6\u901A\u4FE1\uFF0C\u5C31\u5B8C\u5168\u662F\u4F7F\u7528\u666E\u901A\u7684HTTP\u534F\u8BAE\uFF0C\u53EA\u4E0D\u8FC7\u7528&quot;\u4F1A\u8BDD\u5BC6\u94A5&quot;\u52A0\u5BC6\u5185\u5BB9\u3002</p>',32),t=[r];function d(s,o){return l(),i("div",null,t)}var c=e(h,[["render",d],["__file","https.html.vue"]]);export{c as default};
